int Matrix::doFindAngles(double &alpha, double &beta, double &gamma){
	double a, b, c, diff;
	double Solution[6][3];
	int flag, i;

        if(Get_Mij(2, 2) == 1.0) {
		b = 0;
		if(Get_Mij(0, 0) == 0) {
			a = PI/2;
			c = 0;
		} else {
                        a = atan(Get_Mij(0, 1)/Get_Mij(0, 0));
			c = 0;
		}
	} else {
	        b = acos(Get_Mij(2, 2));
	        a = atan(Get_Mij(2, 1)/Get_Mij(2, 0));
	        c = atan(-1*Get_Mij(1, 2)/Get_Mij(0, 2));
	}

	Solution[0][0]=a; Solution[0][1]=b; Solution[0][2]=c;
	Solution[1][0]=a+PI; Solution[1][1]=b; Solution[1][2]=c;
	Solution[2][0]=a; Solution[2][1]=2*PI-b; Solution[2][2]=c;
	Solution[3][0]=a; Solution[3][1]=b; Solution[3][2]=c+PI;
	Solution[4][0]=a+PI; Solution[4][1]=2*PI-b; Solution[4][2]=c;
	Solution[5][0]=a; Solution[5][1]=2*PI-b; Solution[5][2]=c+PI;

	flag = 0;
	for(i=0; i < 6; i++)
	{
		diff = compare(Solution[i][0], Solution[i][1], Solution[i][2]);
		if(diff < 0.1)
		{
			flag = 1;
			alpha = Solution[i][0];
			beta  = Solution[i][1];
			gamma = Solution[i][2];
			return(flag);
		}

	}
        return(flag);

}//end findAngles(double &, double &, double &)

int Matrix::findAngles(double &alpha, double &beta, double &gamma){
        double a, b, c = 0;

        b = acos(Get_Mij(2,2));

        if(b <= 0.0001 && b >= -0.0001){
            a = atan2(Get_Mij(0,1), Get_Mij(0,0));

        }//end if
        else if(b <= PI+0.0001 && b >= PI-0.0001){
            a = atan2(-1.0 * Get_Mij(0,1), -1.0 * Get_Mij(0,0));

        }//end else if
        else{
            a = atan2(Get_Mij(2,1) / sin(b), Get_Mij(2,0) / sin(b));
            c = atan2(Get_Mij(1,2) / sin(b), -1.0*Get_Mij(0,2) / sin(b));

        }//end else

        alpha = a;
        beta = b;
        gamma = c;

        return 1;

}//end doFindAngles(double &alpha, double &beta, double &gamma)

Matrix Matrix::findRotation(double a, double b, double c){
	Matrix R(3, 3);
	R.Set_Mij(0, 0, cos (c) * cos (b) * cos (a) - sin (c) * sin (a));
	R.Set_Mij(0, 1, cos (c) * cos (b) * sin (a) + sin (c) * cos (a));
	R.Set_Mij(0, 2, -cos (c) * sin (b));
	R.Set_Mij(1, 0, -sin (c) * cos (b) * cos (a) - cos (c) * sin (a));
	R.Set_Mij(1, 1, -sin (c) * cos (b) * sin (a) + cos (c) * cos (a));
	R.Set_Mij(1, 2, sin (c) * sin (b));
	R.Set_Mij(2, 0, cos (a) * sin (b));
	R.Set_Mij(2, 1, sin (a) * sin (b));
	R.Set_Mij(2, 2, cos (b));
        return R;

}//end findRotation(double, double, double)

double Matrix::compare(double a, double b, double c){
	double R[9], diff;
	int i;

	R[0] = cosf (c) * cosf (b) * cosf (a) - sinf (c) * sinf (a);
	R[1] = cosf (c) * cosf (b) * sinf (a) + sinf (c) * cosf (a);
	R[2] = -cosf (c) * sinf (b);
	R[3] = -sinf (c) * cosf (b) * cosf (a) - cosf (c) * sinf (a);
	R[4] = -sinf (c) * cosf (b) * sinf (a) + cosf (c) * cosf (a);
	R[5] = sinf (c) * sinf (b);
	R[6] = cosf (a) * sinf (b);
	R[7] = sinf (a) * sinf (b);
	R[8] = cosf (b);

	diff = 0;
	for(i =0; i < 9; i++)
		diff += fabs(Get_Mij(i / 3, i % 3) - R[i]);

        return( diff );
}

Matrix Matrix::getSphereCoords(Matrix R){
    if(R.GetRow() != 3 || R.GetCol() != 3){
        cerr << "Cannot get spherical coordinates of non 3x3 Matrix: ("
             << R.GetRow() << ", " << R.GetCol() << ")" << endl;
        exit(EXIT_FAILURE);

    }//end if
    /*</debug>*cout << R << endl;*</debug>*/

    //set up matrix and angles
    Matrix answer(3,3);
    double theta, phi, r;
    theta = phi = r = 0.0;

    //set answers such that each row is contains theta, phi, and r for x, y, and z respectivly
    for( int i = 0; i < 3; i++){
        //r = sqrt( (t[0])^2 + (t[1])^2 + (t[2])^2 ) where t is x, y, or z column
        r = R.getCol(i).magnitude();

        //set r
        answer.Set_Mij(i, 2, r);

        //theta = acos(z/r)
        theta = acos(R.Get_Mij(2, i) / r);

        //theta^Hat = abs(mod(theta + PI, 2 * PI) - PI)
        theta = fabs(fmod(theta + PI, 2 * PI) - PI);

        /*<debug>*cout << "theta[" << i << "]: " << theta << endl;*</debug>*/
        //set y' to transform of theta^Hat
        answer.Set_Mij(i, 1, (PI / 2) - theta);

        if((fabs(R.Get_Mij(1, i)) < 1e-6) && ((R.Get_Mij(0, i)) < 1e-6))
            //set x' to 0
            answer.Set_Mij(i, 0, 0);

        else{
            //phi = atan2( (y/r) / sin(theta), (x/r) / sin(theta)
            phi = atan2((R.Get_Mij(1, i) / r) / sin(theta),
                        (R.Get_Mij(0, i) / r) / sin(theta));

            //phi^Hat = mod( phi + PI, 2 * PI) - PI
            phi = fmod(phi + PI, 2 * PI) - PI;

            /*<debug>*cout << "phi[" << i << "]: "<< phi << endl;*</debug>*/
            //set x' to transform of phi^Hat
            answer.Set_Mij(i, 0, sin(theta) * phi);

        }//end else

    }//end for
    return answer;

}//end getSphereCoords(Matrix)

double Matrix::magnitude(){
    if(GetRow() > 1){
        cerr << "Cannot get the magnitude of non-vectors (" << GetRow()
             << ", " << GetCol() << ")" << endl;
        exit(EXIT_FAILURE);

    }//end if
    double length = 0;
    for(int i = 0; i < GetCol(); i++)
        length += Get_Mij(0,i) * Get_Mij(0,i);

    return sqrt(length);

}//end magnitude(Matrix U)

//U projected onto *this
double Matrix::scalProj(Matrix U){
    if((GetRow() != U.GetCol()) || GetRow() > 1){
        cerr << "Cannot get projection of non-vectors" << "(" << GetRow() 
             << ", " << GetCol() << ") (" << U.GetCol() << ", " << U.GetRow()
             << ")" << endl;
        exit(EXIT_FAILURE);

    }//end if
    Matrix dot = *this * U;
    return (dot.Get_Mij(0,0) / this->magnitude());

}//end scalProj(Matrix,Matrix)

//U projected onto *this
Matrix Matrix::vecProj(Matrix U){
    return(*this * (scalProj(U) / magnitude()));

}//end vecProj(Matrix,Matrix)

Matrix Matrix::getRx(double theta){
    Matrix Rx(3,3);

    Rx.Set_Mij(0,0,1.0);
    Rx.Set_Mij(0,0,0.0);
    Rx.Set_Mij(0,0,0.0);

    Rx.Set_Mij(1,0,0.0);
    Rx.Set_Mij(1,1,cos(theta));
    Rx.Set_Mij(1,2,0 - (sin(theta)));

    Rx.Set_Mij(2,0,0.0);
    Rx.Set_Mij(2,1,sin(theta));
    Rx.Set_Mij(2,2,cos(theta));

    return Rx;

}//end getRx(double)

Matrix Matrix::getRy(double theta){
    Matrix Ry(3,3);

    Ry.Set_Mij(0,0,cos(theta));
    Ry.Set_Mij(0,0,0.0);
    Ry.Set_Mij(0,0,sin(theta));

    Ry.Set_Mij(1,0,0.0);
    Ry.Set_Mij(1,1,1.0);
    Ry.Set_Mij(1,2,0.0);

    Ry.Set_Mij(2,0,0 - (sin(theta)));
    Ry.Set_Mij(2,1,0.0);
    Ry.Set_Mij(2,2,cos(theta));

    return Ry;

}//end getRy(double)

Matrix Matrix::getRz(double theta){
    Matrix Rz(3,3);

    Rz.Set_Mij(0,0,cos(theta));
    Rz.Set_Mij(0,0,0 - (sin(theta)));
    Rz.Set_Mij(0,0,0.0);

    Rz.Set_Mij(1,0,sin(theta));
    Rz.Set_Mij(1,1,cos(theta));
    Rz.Set_Mij(1,2,0.0);

    Rz.Set_Mij(2,0,0.0);
    Rz.Set_Mij(2,1,0.0);
    Rz.Set_Mij(2,2,1.0);

    return Rz;

}//end getRz(double)

